         // Defines a new type with two fields. 
         struct   Funder   { 
             address   addr ; 
             uint   amount ; 
         } 

         struct   Campaign   { 
             address   beneficiary ; 
             uint   fundingGoal ; 
             uint   numFunders ; 
             uint   amount ; 
             mapping ( uint   =   Funder )   funders ; 
         } 

         uint   numCampaigns ; 
         mapping ( uint   =   Campaign )   campaigns ; 

         function   newCampaign ( address   beneficiary ,   uint   goal )   public   returns ( uint   campaignID )   { 
             campaignID   =   numCampaigns ++ ;   // campaignID is return variable 
             // Creates new struct and saves in storage.We leave out the mapping type. 
             campaigns [ campaignID ]   =   Campaign ( beneficiary ,   goal ,   0 ,   0 ); 
         } 

         function   contribute ( uint   campaignID )   public   payable   { 
             Campaign   storage   c   =   campaigns [ campaignID ]; 
             // Creates a new temporary memory struct, initialised with the given values 
             // and copies it over to storage. 
             // Note that you can also use Funder(msg.sender, msg.value) to initialise. 
             c . funders [ c . numFunders ++ ]   =   Funder ({ addr :   msg . sender ,   amount :   msg . value }); 
             c . amount   +=   msg . value ; 
         } 

         function   checkGoalReached ( uint   campaignID )   public   returns ( bool   reached )   { 
             Campaign   storage   c   =   campaigns [ campaignID ]; 
             if ( c . amount     c . fundingGoal ) 
                 return   false ; 
             uint   amount   =   c . amount ; 
             c . amount   =   0 ; 
             c . beneficiary . transfer ( amount ); 
             return   true ; 
         } 
     }   合约没有提供众筹合约的全部功能，但它包含了理解结构所必需的基本概念。
结构类型可以用在映射和数组中，它们本身可以包含映射和数组。  尽管结构本身可以是映射成员的值类型，但结构不可能包含它自己类型的成员。
这个限制是必要的，因为结构的大小必须是有限的。  请注意，在所有函数中，结构类型被分配给(缺省存储数据位置的)局部变量。
这不会复制该结构，而只会存储一个引用，以便分配给本地变量的成员实际写入该状态。  当然，你也可以直接访问结构体的成员，而不必将它分配给局部变量，就像 campaign [campaignID] .amount = 0 一样。  映射  映射类型被声明为 映射(_KeyType =  _ValueType) 。
这里 _KeyType 几乎可以是任何类型，除了映射，动态调整大小的数组，合约，枚举和结构。 _ValueType 实际上可以是任何类型，包括映射。  映射可以看作是 哈希表 ，它们被虚拟初始化，这样每个可能的键都存在并映射到一个字节表示全为零的值：一个类型, 默认值 默认值 。
但是，相似性在此处结束：关键数据实际上并不存储在映射中，只有其用于查找值的 keccak256 哈希值。  因此，映射没有长度或 设置 键或值的概念。  映射只能用于状态变量(或作为内部函数中的存储引用类型)。  可以将映射标记为public，并使Solidity创建一个 getter  。 _KeyType 将成为getter的必需参数，它将返回 _ValueType 。  _ValueType 也可以是一个映射。
getter将递归地为每个 _KeyType 使用一个参数。       pragma   solidity   ^ 0.4.0 ; 

     contract   MappingExample   { 
