library GetCode {
    function at(address _addr) public view returns (bytes o_code) {
        assembly {
            // 检索代码的大小，这需要大会
            let size := extcodesize(_addr)
            // 分配输出字节数组 - 这也可以通过使用`o_code = new bytes(size)`来进行组装
            o_code := mload(0x40)
            // 新的“内存结束”包括填充
            mstore(0x40, add(o_code, and(add(add(size, 0x20), 0x1f), not(0x1f))))
            // 在存储器中存储长度
            mstore(o_code, size)
            // 实际上检索代码，这需要大会
            extcodecopy(_addr, add(o_code, 0x20), 0, size)
        }
    }
}  在优化器无法生成高效代码的情况下，内联汇编也可能是有益的。
请注意，由于编译器不执行检查，所以汇编更难以编写，因此只有在您确实知道自己在做什么时，才应该将它用于复杂的事情。  pragma solidity ^0.4.16;

library VectorSum {
    // 此函数效率较低，因为优化器当前无法删除数组访问中的边界检查。
    function sumSolidity(uint[] _data) public view returns (uint o_sum) {
        for (uint i = 0; i   _data.length; ++i)
            o_sum += _data[i];
    }

    // 我们知道我们只能访问数组的边界，所以我们可以避免检查。
    // 需要将0x20添加到数组中，因为第一个插槽包含数组长度。
    function sumAsm(uint[] _data) public view returns (uint o_sum) {
        for (uint i = 0; i   _data.length; ++i) {
            assembly {
                o_sum := add(o_sum, mload(add(add(_data, 0x20), mul(i, 0x20))))
            }
        }
    }

    // 与上面相同，但在内联汇编中完成整个代码。
    function sumPureAsm(uint[] _data) public view returns (uint o_sum) {
        assembly {
           // 加载长度(前32个字节)
           let len := mload(_data)

           // 跳过长度字段。
           // 保持临时变量，以便它可以在原地增加。
           // 注意：增加_data会导致此组合块后不可用的_data变量
           let data := add(_data, 0x20)

           // 迭代，直到不符合约束。
           for
               { let end := add(data, len) }
               lt(data, end)
               { data := add(data, 0x20) }
           {
               o_sum := add(o_sum, mload(data))
           }
        }
    }
}

